INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12856, '你没看见我正忙着吗！再这样拖沓，我们是不可能在王子殿下归来之前修好这座基地啦！', '', '要不是那群混蛋烧毁了船只，我们早就到家啦。别来烦我了，我要回去工作啦！', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12857, '朋友，银色北伐军的部队已经抵达此地。我们不会让你独占所有荣耀的！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12858, '让那群该死的农夫赶紧干活啊！我好想回到铁炉堡。实在是太想念我的老婆和孩子了。', '', '船长说，只要阿尔萨斯王子殿下回来，我们就能返回洛丹伦了。', '', '不知道王子殿下究竟是被什么事给耽搁了，但是我们真的坚持不了太久了。许多士兵都无法忍受如此严寒的气候。', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12859, '看着那些被野蛮人烧毁的船只，我们的心情仿佛跌到了谷底。说实话，我从来没有觉得如此无助……', '', '愤怒。看着眼前这堆烂摊子，我实在是压抑不住心中的怒火。我们历尽艰辛，一路披荆斩棘穿越森林才来到海边，却发现还是迟了一步。返回家园的唯一希望已经破灭了。', '', '尽管这片冰霜废土给我们留下了痛苦的回忆，但我相信，年轻的王子殿下终将领导我们走向胜利。我夜夜祈祷，希望他能平安归来。', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12860, '<被遗忘的步兵摇了摇头。>$B$B我们快要支撑不下去了。阿尔萨斯王子殿下再不回来的话，我们都会没命的。', '', '我们不可能及时备好船只了。寒冬降临时，就是我们的末日。恐怕我们都被王子殿下遗弃了。', '', '阿尔萨斯殿下和穆拉丁殿下离开这里很长时间了。希望他们平安。', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12862, '今天你想去哪儿呢？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12863, '加入上等的俱乐部可以为你的生活增添别样的风采。', '加入上等的俱乐部可以为你的生活增添别样的风采。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (12864, '嘿，伙计！你手里拿的不是美酒节奖币吗？我恰好有一些美酒节物品要出售。', '嘿，伙计！你手里拿的不是美酒节奖币吗？我恰好有一些美酒节物品要出售。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
