INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (13510, '嗯……你有点面熟……让我想想……$B$B哈哈！西部荒野！我在西部荒野见过你。你是$N！咱们又见面啦，希望你能再次伸出援手。$B$B这里离西部荒野非常遥远，但是如果我们打不赢这一战，恐怕就要无家可归了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (13511, '$N！你换了新衣服，差点认不出你啦。你还留着那件外套吗……或者你选了马裤……还是法杖？我不记得了，那已经是很久之前的事情了吧……$B$B嗯，很高兴你能来这里帮忙。我们需要一切可以动用的力量。$N，如果你的表现能像在西部荒野帮助人民军时一样好，那我们就有希望了。$B$B眼前的局势非常严峻，赢不了这一战，我们就休想回到西部荒野了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (13512, '$N，有了圣光的祝福和你的帮助，迪菲亚已经不再构成威胁。$B$B但是我们距离西部荒野非常遥远，而且如果我们打不赢这一战，恐怕就要无家可归了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
