INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14326, '凡人们，感谢你们让我摆脱了弥漫在这片土地上的堕落力量。尽管我尚未完全恢复，但我能帮助你们与尤格-萨隆作战。$B我将冰霜之韧赐予你和你的朋友们，以此提高你们造成的伤害并减少敌人对你们的伤害。如果你们当中有谁命悬一线，我可以将他冻结成冰，在短时间内使他免受任何伤害。这一技能需要消耗大量的精力，因此施放后我必须休息一段时间。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
