INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14431, '$C，很高兴见到你。$B$B我负责向候选者传授骑乘近身战的诀窍。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14434, '近战目标可以帮助候选者训练骑乘攻击与防御的技巧。$B$B你需要靠近并刺击近战目标。刺击的力量通常会令目标旋转起来，对你或你的坐骑造成伤害。因此，你需要巧妙地利用防御技能。$B$B在攻击目标之前，最好确保自己至少有一层防御效果。在接下来的战斗中，你还需要尽可能地堆叠并刷新防御效果。否则，你很快就会被打下坐骑。$B$B在骑乘实战中遵循着同样的原则：在攻击与防御之间寻求平衡，一有机会就发动刺击，并尽可能保持多层防御效果。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14436, '嗨，你好！$B$B你是来学习冲锋技巧的吗，$C？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14437, '好吧！骑乘冲锋可不仅仅是让你的坐骑高速冲刺，体验那种心飙到嗓子眼的快感……当然这的确是最有趣的部分，嘿嘿！但是冲锋目标可以帮助你掌握更多诀窍。$B$B冲锋的关键在于距离与时机。不能靠得太近，否则无法全力提速。也不能离得太远，否则坐骑就会进入疲惫状态！至于冲锋的时机……$B$B在冲锋之前，你最好确认一下冲锋目标身上是否有防御效果。如果有，你应该先使用碎盾然后再冲锋！$B$B实战中遵循着同样的原则：在冲锋前要尽可能地削弱对手的防御效果！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14438, '哎呀。又一位$C，你是来学习碎盾的技巧的吗……？', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14439, '远程碎盾攻击上手容易精通难。碎盾攻击所能造成的伤害不高，但是配合刺击和冲锋使用就能发挥出完美的效果，令你在骑乘作战中所向披靡。$B$B选定一个远程目标，估算一下碎盾攻击的射程……你需要多远的距离以及你能将长枪掷多远。$B$B接下来，就开始投掷长枪吧，直到你破坏目标身上的所有防御效果。$B$B骑乘实战中遵循着同样的原则：彻底粉碎对手的防御……然后狠狠地揍他们！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14441, '朋友，你来得正是时候。很快我就会给你安排更多的任务了……', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
