INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14921, '牧师们都在军事区的外环区域。', '牧师们都在军事区的外环区域。', '去军事区外环区域看看吧。那里的牧师努力确保没有哪个断掉的肋骨长时间得不到修复。', '去军事区外环区域看看吧。那里的牧师努力确保没有哪个断掉的肋骨长时间得不到修复。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14922, '盗贼们都呆在盗贼区。就这么简单。你可以在外环区域找到他们。', '盗贼们都呆在盗贼区。就这么简单。你可以在外环区域找到他们。', '你可以在盗贼区外环区域找到那些潜行者。', '你可以在盗贼区外环区域找到那些潜行者。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14923, '基里安·哈根是这里的剥皮训练师。他就在盗贼区的内环区域。', '基里安·哈根是这里的剥皮训练师。他就在盗贼区的内环区域。', '如果你想学习剥皮，就要前往盗贼区。剥皮训练师的铺位就在内环区域。', '基里安·哈根在盗贼区传授剥皮的技能。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14924, '如果你真的想把宠物委托给某个亡灵，那你可以和安雅·玛尔雷谈谈，他就呆在贸易区上层的旅馆里。', '如果你真的想把宠物委托给某个亡灵，那你可以和安雅·玛尔雷谈谈，他就呆在贸易区上层的旅馆里。', '你可以同安雅·玛尔雷谈一谈，他就在贸易区上层的旅店旁边。我并不建议你这样做，不过它们毕竟是你的宠物。', '你可以同安雅·玛尔雷谈一谈，他就在贸易区上层的旅店旁边。我并不建议你这样做，不过它们毕竟是你的宠物。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14925, '这里的裁缝叫乔瑟夫·格里高利。他就在魔法区的内环区域。', '这里的裁缝叫乔瑟夫·格里高利。他就在魔法区的内环区域。', '先到魔法区，然后朝内环区域走，裁缝店就在那里。', '先到魔法区，然后朝内环区域走，裁缝店就在那里。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14926, '你竟然有胆量问我有关术士的事。到魔法区的外环区域看看吧，现在给我消失。', '你竟然有胆量问我有关术士的事。到魔法区的外环区域看看吧，现在给我消失。', '你可以在魔法区的主建筑区域找到那些术士，我希望告诉你这些不会引起什么麻烦。', '你可以在魔法区的主建筑区域找到那些术士，我希望告诉你这些不会引起什么麻烦。', '', '', '', '', '', '', '', '', '', '', '', '');
