INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14928, '到军事区内环区域找到阿基巴德，他可以教你如何某些使用武器。', '到军事区内环区域找到阿基巴德，他可以教你如何某些使用武器。', '很高兴有人愿意学点儿真正有用的东西。到军事区的内环区域去找阿基巴德吧。', '很高兴有人愿意学点儿真正有用的东西。到军事区的内环区域去找阿基巴德吧。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14929, '你想离开这，这我不怪你。先到最顶层，然后朝废墟的北面走。坐上两座塔那的地精飞艇就可以离开了。', '你想离开这，这我不怪你。先到最顶层，然后朝废墟的北面走。坐上两座塔那的地精飞艇就可以离开了。', '飞艇塔就在幽暗城的北面。', '飞艇塔就在幽暗城的北面。', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14931, '在这看到兽人感到意外吧？用不着这样。$B$B需要有人看着那些被遗忘者。如果从一开始我们就好好盯着，或许天谴之门的事根本就不会发生。', '在这看到兽人感到意外吧？用不着这样。$B$B需要有人看着那些被遗忘者。如果从一开始我们就好好盯着，或许天谴之门的事根本就不会发生。', '希望你要找的不是那些憎恶。炼金师没有一个靠得住，他们造出来的那些在大厅里巡逻的东西也一样。真正保卫这里安全的是我们。', '希望你要找的不是那些憎恶。炼金师没有一个靠得住，他们造出来的那些在大厅里巡逻的东西也一样。真正保卫这里安全的是我们。', 'Lok\'tar。不要在这里做出任何可疑的行为。如果那些亡灵打算加入部落，他们就得接受我们的监视。', 'Lok\'tar。不要在这里做出任何可疑的行为。如果那些亡灵打算加入部落，他们就得接受我们的监视。', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14933, '怎么了？骨头堆。在找人吗？这里只有我，要买东西就快点儿。', '怎么了？骨头堆。在找人吗？这里只有我，要买东西就快点儿。', '我敢说你们这些无赖一定觉得自己在天谴之门干得很漂亮，对吧？游戏结束了。我们来就是要阻止你们的一切行为。', '我敢说你们这些无赖一定觉得自己在天谴之门干得很漂亮，对吧？游戏结束了。我们来就是要阻止你们的一切行为。', '我会告诉你都该知道些什么，但这并不表示我有多么愿意。我们一直在监视着你和你的同类。', '我会告诉你都该知道些什么，但这并不表示我有多么愿意。我们一直在监视着你和你的同类。', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14934, '坑里臭气熏天，还有一群蛇围着我。不过我来这是有事要做的。$B$B自从普特雷斯在天谴之门上演了一出好戏，酋长便不再信任那些人，而是离他们远远的。这就是为什么我们会在这里。那些药剂师离了我什么都做不了。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14940, '我们的新…客人对我们的工作的确有些拖累，不过在从事种族灭绝工作中保持机警是没错的。也许，在适当的时候他们会认识到普特雷斯就是个倒霉的异类，并且允许我们安安稳稳地继续我们的工作。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (14984, '我早知道我会受到这种冷遇。长久以来我们一直在隐居，因为我们必须要与族人分离。$B$B那段时间已经过去。不管这颗幼树之荫下的居民是否会接受我们，上层精灵们都要摒弃过去的身份，从黑暗中返回光明。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (15005, '你会想看看幽暗城的模样。它就坐落在南边的旧洛丹伦废墟，你会找到一个下行的升降梯。等你到了，幽暗城的卫兵会给你更详细的指引。', '你会想看看幽暗城的模样。它就坐落在南边的旧洛丹伦废墟，你会找到一个下行的升降梯。等你到了，幽暗城的卫兵会给你更详细的指引。', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (15037, '有人不相信传说中的暗夜精灵莫霍克。$B$B真为他们悲哀。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (15038, '幸会，莫霍克的好$G兄弟:姐妹;。', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (15040, 'A few weeks in this pathetic castle will give us time to work on some new products and let others forget about the unfortunate... misunderstanding in Dragonblight.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `locales_npc_text` (`entry`, `Text0_0_loc4`, `Text0_1_loc4`, `Text1_0_loc4`, `Text1_1_loc4`, `Text2_0_loc4`, `Text2_1_loc4`, `Text3_0_loc4`, `Text3_1_loc4`, `Text4_0_loc4`, `Text4_1_loc4`, `Text5_0_loc4`, `Text5_1_loc4`, `Text6_0_loc4`, `Text6_1_loc4`, `Text7_0_loc4`, `Text7_1_loc4`) VALUES (15066, '在这见到我吃惊吗？每个人都需要干净衣服，除了找你的水元素帮忙，还有更好的办法吗？$B$B只需把你的衣服拿来，象征性的付点钱，不知不觉中你的衣服又变得清爽、干净！', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
