UPDATE npc_text SET text0_0='沿着北边的道路去奥格玛。你可以问问那儿的守卫烹饪训练师在哪儿。',text0_1='沿着北边的道路去奥格玛。你可以问问那儿的守卫烹饪训练师在哪儿。' WHERE Id=4022;
