UPDATE npc_text SET text0_0='进入灌木丛要小心点，$c。有个具伤害力的光环徘徊在那个地方。那一定是以前摧毁曾在这里的德鲁伊的生物残渣。',text0_1='' WHERE Id=9391;
UPDATE npc_text SET text0_0='这片森林是个怪异的地方，这里的坏魂能要比联盟还多，所以你自己得小心点。',text0_1='' WHERE Id=9392;
